import React,{useState} from 'react'
import styles from './form.module.css'


class Page extends React.Component {
      constructor(){
        const [email, setEmail] = useState('')
        const [title, setTitle] = useState('')
        const [introduction, setIntroduction] = useState('')
      }

    render() {
        return (
            <div className={styles.form}>
                <form onSubmit={()=>{alert(title)}}>
               <input className={styles.input} onChange={e => setTitle(e.target.value)} type="text" placeholder="title"></input>
               <br></br>
               <input className={styles.input} onChange={this.handleChangeemail} type="email" placeholder="email"></input>
               <br></br>
               <textarea className={styles.textarea} onChange={this.handleChangeemailarea} name="textarea" placeholder="tipe"></textarea>
              <br></br>
               <button className={styles.submit}> Submit</button>
           </form>
            </div>

        )

    }

}

export default Page
